import { render } from '@testing-library/react';
import React, {Component} from 'react';

class Btns extends Component {
    constructor(props) {
        super(props);
        this.state = {
            hover: false
        };
    }

    hoverHandler(event) {
        console.log(
            "overed"
        )
        this.setState({
            hover: true
        });
    }

    render() {
        const first = this.props.isFirst ? "offset-lg-2" : "";
        const hover = this.state.hover ? "btn-outline-primary" : "btn-primary";

        return (
            <div className={"col-md-3 col-lg-2" + " " + first}>
              <button type='button' className={"btn btn-lg" + " " + hover} onMouseOver={()=>this.hoverHandler()}>{this.props.name}</button>
            </div>
        )
    }
}

export default Btns
